import React, { useState/*, useEffect*/, useContext } from "react";
import { userContext } from "../../App"
import "../../styles/Singin.css";
import { Link, useHistory } from "react-router-dom";
import M from "materialize-css";
import logo from "../../images/logo.png"

const Login = () => {
  const history = useHistory();
  const [mail, setmail] = useState(null);
  const [password, setpassword] = useState(null);
  const { /*state, */dispatch } = useContext(userContext);


  const PostData = () => {
    fetch("https://exposicion-virtual.herokuapp.com/login/admin", {
      method: "POST",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        "password": password,
        "userName": mail
      }),
    })
      .then((res) => {
        if (!res.ok) {
          M.toast({ html: "datos invalidos o el Administrador no existe", classes: "#c62828 red darken-3" });
        } else {
          localStorage.setItem("user", "admin");
          dispatch({ type: "USER", payload: "admin" });
          M.toast({
            html: "Ha iniciado como Administrador",
            classes: "#388e3c green darken-2",
          });
          history.push("/");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className="mycard">
      <div id="fondoTarjetaLogin" className="card auth-card input-field">
        <img alt="logo" className="logo-login" src={logo} />
        <input
          type="text"
          id='inputLogin'
          placeholder="Ingrese usuario"
          value={mail}
          onChange={(e) => setmail(e.target.value)}
        />
        <input
          type="password"
          id='inputLogin'
          placeholder="Ingrese contraseña"
          value={password}
          onChange={(e) => setpassword(e.target.value)}
        />
        <button
          id="botonLogin"
          className="btn waves-effect waves-light #64b5f6 red darken-1"
          onClick={() => PostData()}
        >
          Ingresar
            </button>
        <h5 id="H5Register">
          <Link id="linkRegister" to="/login">Ingresar como usuario</Link>
          <tr />
          <Link id="linkRegister" to="/register">Registrate acá</Link>
        </h5>
      </div>
    </div>
  );
};

export default Login;
